import React, { Component, useState } from 'react'


function AddTodo() {
  //Using Hooks
  const [todo, setTodo] = useState("")


  return (
    <div>
      <form>
        <label>
          Add your todo:
          <input type="text" name="name" onChange={() => setTodo(this)} />
        </label>
        <label>
          Your currently task is {todo}
          <input type="submit" value="Submit" />
        </label>
      </form>

    </div>
  )
}

export default AddTodo
